package com.cadrlife.service;import javax.ws.rs.ext.MessageBodyReader;import javax.ws.rs.ext.MessageBodyWriter;import com.fasterxml.jackson.jaxrs.json.JacksonJsonProvider;import com.google.inject.Guice;import com.google.inject.Injector;import com.google.inject.servlet.GuiceServletContextListener;import com.sun.jersey.guice.JerseyServletModule;import com.sun.jersey.guice.spi.container.servlet.GuiceContainer;import com.cadrlife.service.service.PersonResource;import com.cadrlife.service.service.PersonService;import com.cadrlife.service.service.StatusResource;import com.cadrlife.service.servlet.*;public class WebAppConfig extends GuiceServletContextListener {	@Override	protected Injector getInjector() {		return Guice.createInjector(new JerseyServletModule() {			@Override			protected void configureServlets() {				bind(StatusResource.class);				bind(PersonResource.class);				bind(PersonService.class);				bind(MessageBodyReader.class).to(JacksonJsonProvider.class);			    bind(MessageBodyWriter.class).to(JacksonJsonProvider.class);				serve("/api/*").with(GuiceContainer.class);				filter("/blah").through(LifeCycleFilter.class);			}		});	}}